import styled from 'styled-components';

export const StyledSplash = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;

    h1 {
        font-size: 7em;
    }

    p {
        font-size: 1.7em;
        margin-bottom: 20px;
    }
`;
