import styled from 'styled-components';

export const StyledSplash = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    position: relative;
    flex: 1;

    h1 {
        font-size: 10em;
        position: abosolute;
        top: 0;
        color: #1c1c1c;
    }

    p {
        font-size: 1.7em;
        margin-bottom: 20px;
    }
`;
